import React, {useContext} from 'react';
import LanguageContext from '../../components/LanguageContext';
import Colchis from '../../img/Colchis.png'



const Project = props => {

    const language = useContext(LanguageContext)
    const {number, title, technos, description, background}  = props;

    return (
        <article className="content__item">

            <div className="img-wrap img-wrap--content">
				<div className="img img--content" style={{ backgroundImage : background }} ></div>
			</div>

			<header className="content__item-header">
				<span className="content__item-header-meta">{technos}</span>
				<h3 className="content__item-header-title">{title}</h3>
			</header>

			<div className="content__item-copy">
				<p className="content__item-copy-text">
					{language.language === "Shakespeare" ? description.en : description.fr}
				</p>
			</div>

		</article>
    );
}

export default Project;
