import React, { useContext } from 'react';
import LanguageContext from '../components/LanguageContext';

const ParametresBanner = () => {

    const language = useContext(LanguageContext);
console.log(language)
    return (
        <div className="parametresBanner">
            <div className="languages">
                <label htmlFor="Moliere">Moliere</label>
                <input type="radio" id="Moliere" name="language" value="Moliere" onSelect={(e)=>language.updateLanguage(e.value)}/>

                <label htmlFor="Shakespeare">Shakespeare</label>
                <input type="radio" id="Shakespeare" name="language" value="Shakespeare" />
            </div>
        </div>
    );
};

export default ParametresBanner;
