import React from 'react';

const ArticleAbout = props => {

    const {image, height, name, firstName, infos} = props;

    return (
        <li 
            className="article-container" style={{height: `${height}%`}}    
        >
            <div className="article" style={{backgroundImage: `url(${image})`}}>
                <div className="name">
                   {name} 
                </div>
                <div className="firstName">
                    {firstName}
                </div>
            </div>

            <div className="infos">
                <h5 className="title">{infos.fr.title}</h5>
                <p className="paragraphe">{infos.fr.paragraphe}</p>
            </div>

        </li>

    )
}

export default ArticleAbout;
