import React, {useState, useEffect} from 'react';

const ArticleAbout = props => {


    const [active, setActive] = useState(false);
    const {imageNb, imageC, height, name, firstName, infos} = props;

    useEffect(() =>{

            console.log(document.querySelector('.article-container'))
            setActive(!active)

        },[active])

    return (
        <li className="article-container" style={{height: `${height}%`}} >

            <div 
                className="article" 
                style={ active === false ? {backgroundImage: `url(${imageNb})`} : {backgroundImage: `url(${imageC})`} }
                onClick={()=>console.log(active)}
            >

                <div className="name">
                   {name} 
                </div>
                <div className="firstName">
                    {firstName}
                </div>
            </div>

            <div className="infos">
                <h5 className="title">{
                    infos.fr.title.split('').map((letter,i) => {
                        if(i%2 === 0){
                            return <span key={i} style={{transform: `translateY(${Math.floor(Math.random() * 25) + 1  }%)`}}>{letter}</span>
                        }else{
                            return <span key={i} style={{transform: `translateY(-${Math.floor(Math.random() * 25) + 1  }%)`}}>{letter}</span>
                        }

                    })

                }</h5>
                <p className="paragraphe">{infos.fr.paragraphe}</p>
            </div>

        </li>

    )
}

export default ArticleAbout;
