import React, {useEffect} from 'react';

import { projects } from "../../data/projects-data";
import Project from "./Project";
//import { pieces } from '../../script/pieces';


const Projects = () => {

    const { project1, project2, project3} = projects;

    useEffect(() => {


        let animejs = document.createElement("script");
       let piecesjs = document.createElement("script");
       let mypieces = document.createElement("script"); 

       animejs.src = "./assets/anime/lib/anime.min.js";
       piecesjs.src = "./assets/pieces/dist/pieces.min.js";
       mypieces.src = "./assets/mypiece/pieces.js";

       animejs.id = "animeJs";


       animejs.async = false;
       piecesjs.async = false;
       mypieces.async = false; 

       document.body.appendChild(animejs);
       document.body.appendChild(piecesjs);
       document.body.appendChild(mypieces);





    }, []);


    return (
        <main className="projects-container js">
            <div className="pieces-slider">
                <Project project={project1} /> 
                <Project project={project2} /> 
                <Project project={project3} />

                <canvas className="pieces-slider__canvas"></canvas>

                <button className="pieces-slider__button pieces-slider__button--prev">prev</button>
                <button className="pieces-slider__button pieces-slider__button--next">next</button>
            </div>          
        </main>
    )
}

export default Projects;
