import React, { useState } from 'react';

const IsometricBlock = props => {

    const [ hoverState, setHoverState ] = useState(false);
    const handleMouseEnter = (e) => {
        setHoverState(true);
    }

      const handleMouseLeave = () => {
        setHoverState(false);
      }




    return (
        <li 
            key={props.language} 
            className="iso-block"
            onMouseLeave={handleMouseLeave} 
            onMouseEnter={handleMouseEnter}
        >
            {[...Array(props.number)].map((number, i)=>{
               return  <span key={i}></span>
            })}
            <span style={{...(hoverState && {transform:`translate(${props.number*10}px, -${(props.number+1)*10}px)`})}}>
                <props.icon  style={{color: props.color}} size={30}/>
            </span>
        </li>
    );
};

export default IsometricBlock;
