import React from 'react';
import { NavLink } from 'react-router-dom';


const Navigation = () => {

    const handleMouseOver = (e)=>{
        document.querySelector('.nav-active').classList.add('disable');
    }
    const handleMouseOut = ()=>{
        document.querySelector('.nav-active').classList.remove('disable');
    }

    return (

        <nav className="navigation">
            <div className="brand">NK</div>

            <ul onMouseEnter={(e)=> handleMouseOver(e)} onMouseLeave={()=> handleMouseOut()}>
                <NavLink to='/' exact activeClassName='nav-active' >
                    <li>Home</li>
                </NavLink>

                <NavLink to='/about' exact activeClassName='nav-active' >
                    <li>About</li>
                </NavLink>

                <NavLink to='/skills' exact activeClassName='nav-active' >
                    <li>Skills</li>
                </NavLink>

                <NavLink to='/works' exact activeClassName='nav-active' >
                    <li>Works</li>
                </NavLink>

                <NavLink to='/contact' exact activeClassName='nav-active' >
                    <li>Contact</li>
                </NavLink>
            </ul> 

            <div className="location">
                <p>Strasbourg</p>
                <p>France</p>
            </div>
        </nav>
    );
};

export default Navigation;
