import React, {useEffect} from 'react'

const Mouse = () => {


    useEffect(() => {
        const smallBall = document.querySelector('.cursor__ball--small');
        const bigBall = document.querySelector('.cursor__ball--big');


        const handleCursor = (e) =>{    
            let bigPosX = e.pageX - 15;
            let bigPosY = e.pageY - 15;

            let delay = 6,
                revisedPosX = 0,
                revisedPosY = 0;

            revisedPosX += (bigPosX - revisedMousePosX) / delay;
            revisedPosY += (bigPosY - revisedMousePosY) / delay;

            smallBall.style.top = (e.pageY-5) + "px";
            smallBall.style.left = (e.pageX - 5) + "px";
            bigBall.style.top = revisedPosY + "px";
            bigBall.style.left = revisedPosX + "px";
            /*
            bigBall.style.top = (e.pageY-15) + "px";
            bigBall.style.left = (e.pageX-15) + "px";
            */
        }

        const handleHover = () =>{
            smallBall.classList.add('hovered');
        }
        const handleLeave = () =>{
            smallBall.style.transition = ".3s ease-out";
            smallBall.classList.remove('hovered');
        }

        window.addEventListener('mousemove', handleCursor);

        document.querySelectorAll('.hover').forEach(link =>{
            link.addEventListener('mouseover', handleHover );
            link.addEventListener('mouseleave', handleLeave);
        })
    })

    return (

        <div className="mouse">
            <div className="cursor__ball cursor__ball--big ">

            </div>

            <div className="cursor__ball cursor__ball--small">

            </div>
      </div>


    );
}

export default Mouse;
