import React, { useContext} from 'react';

import Navigation from '../components/Navigation';
import ParametresBanner from '../components/ParametresBanner';
import LanguageContext from '../components/LanguageContext';
import SocialNetwork from '../components/SocialNetwork';
import ArticleAbout from '../components/ArticleAbout';

import NB1 from '../img/NB1.jpg';
import NB2 from '../img/NB2.jpg';
import NB3 from '../img/NB3.jpg';
import C1 from '../img/C1.jpg';
import C2 from '../img/C2.jpg';



const About = () => {

    const language = useContext(LanguageContext);

    const handleClick = (e)=>{
        if( document.querySelector('.active')){
            console.log(document.querySelector('.active'))
          document.querySelector('.active').classList.remove('active');  
        }
        e.target.parentNode.classList.add('active')
    }

    return (
        <div className='about'>

            <Navigation />
            <ParametresBanner/>

            <div className="container">

                <div className="title">
                    <h2>{language.language === "Shakespeare" ? ".About" : ".A propos"}</h2> 
                </div>

                <ul className="images-container" onClick={(e)=> handleClick(e)}>
                    <ArticleAbout image={C2} height={30} letter={'n'}/>
                    <ArticleAbout image={C1} height={40} letter={'i'}/>
                    <ArticleAbout image={NB2} height={50} letter={'c'}/>
                    <ArticleAbout image={NB3} height={65} letter={'o'}/>
                    <ArticleAbout image={NB1} height={50} letter={'l'}/>
                    <ArticleAbout image={C2} height={40} letter={'a'}/>
                    <ArticleAbout image={C2} height={30} letter={'s'}/>
                </ul>


                <SocialNetwork/>
            </div>
        </div>
    );
};

export default About;
