import React, { useContext} from 'react';

import Navigation from '../components/Navigation';
import ParametresBanner from '../components/ParametresBanner';
import LanguageContext from '../components/LanguageContext';
import SocialNetwork from '../components/SocialNetwork';
import ArticleAbout from '../components/ArticleAbout';

import NB1 from '../img/NB1.jpg';
import NB2 from '../img/NB2.jpg';
import NB3 from '../img/NB3.jpg';
import C1 from '../img/C1.jpg';
import C2 from '../img/C2.jpg';



const About = () => {

    const language = useContext(LanguageContext);

    return (
        <div className='about'>

            <Navigation />
            <ParametresBanner/>

            <div className="container">

                <div className="title">
                    <h2>{language.language === "Shakespeare" ? ".About" : ".A propos"}</h2> 
                </div>

                <div className="images-container">
                    <ArticleAbout image={C1}/>
                    <ArticleAbout image={NB2}/>
                    <ArticleAbout image={NB3}/>
                    <ArticleAbout image={NB1}/>
                    <ArticleAbout image={C2}/>
                </div>


                <SocialNetwork/>
            </div>
        </div>
    );
};

export default About;
