import React, { useContext} from 'react';

import Navigation from '../components/Navigation';
import ParametresBanner from '../components/ParametresBanner';
import LanguageContext from '../components/LanguageContext';
import SocialNetwork from '../components/SocialNetwork';
import ArticleAbout from '../components/ArticleAbout';

import NB1 from '../img/NB1.jpg';
import NB2 from '../img/NB2.jpg';
import NB3 from '../img/NB3.jpg';
import C1 from '../img/C1.jpg';
import C2 from '../img/C2.jpg';

import {photos} from '../data/about-data';

const About = () => {

    const language = useContext(LanguageContext);
    const { photo_1} = photos;

    const handleClick = (e)=>{

        if (e.target.parentNode.classList.contains('active')) {
            e.target.parentNode.classList.remove('active')
        }else{
            if( document.querySelector('.active')){
          document.querySelector('.active').classList.remove('active');  
        } 
            e.target.parentNode.classList.add('active');           
        }     

    }

    return (
        <div className='about'>

            <Navigation />
            <ParametresBanner/>

            <div className="container">

                <div className="title">
                    <h2>{language.language === "Shakespeare" ? ".About" : ".A propos"}</h2> 
                </div>

                <ul className="images-container" onClick={(e)=> handleClick(e)}>
                    <ArticleAbout image={C2} height={30} name={'N'} firstName={'K'} infos={photo_1}/>
                    <ArticleAbout image={C1} height={40} name={'i'} firstName={'l'} infos={photo_1}/>
                    <ArticleAbout image={NB2} height={50} name={'c'} firstName={'i'} infos={photo_1}/>
                    <ArticleAbout image={NB3} height={65} name={'o'} firstName={'p'} infos={photo_1}/>
                    <ArticleAbout image={NB1} height={50} name={'l'} firstName={'f'} infos={photo_1}/>
                    <ArticleAbout image={C2} height={40} name={'a'} firstName={'e'} infos={photo_1}/>
                    <ArticleAbout image={C2} height={30} name={'s'} firstName={'l'} infos={photo_1}/>
                </ul>


                <SocialNetwork/>
            </div>
        </div>
    );
};

export default About;
