        e.preventDefault();
        let nameInput = document.getElementById('name');
        let mailInput = document.getElementById('mail');
        let phoneInput = document.getElementById('phone');
        let subjectInput = document.getElementById('subject');
    }

    return (
        <form>
            <div className="coord-line">
                <input 
                    type="text"
                    id="name"
                    name="name"
                    required
                    onChange={(e)=> setName(e.target.value)}
                    value={name}
                    placeholder={language.language === "Shakespeare" ? en.name : fr.name }
                />

                <div className="email-content">
                <label id="not-mail"></label>
                    <input
                        type="mail"
                        id="email"
                        name="email"
                        required
                        onChange={(e) => setMail(e.target.value)}
                        placeholder="Email *"
                        value={mail}
                    />
                </div>
                <input
                    type="number"
                    id="phone"
                    name="phone"
                    onChange={(e) => setPhone(e.target.value)}
                    placeholder={language.language === "Shakespeare" ? en.phoneL : fr.phoneL }
                    value={phone}
                />
            </div>
            <input 
                    type="text"
                    id="subject"
                    required
                    name="subject"
                    onChange={(e)=> setSubject(e.target.value)}
                    value={subject}
                    placeholder={language.language === "Shakespeare" ? en.subject : fr.subject }
            />
            <textarea
                id="message"
                name="message"
                onChange={(e) => setMessage(e.target.value)}
                placeholder="message *"
                value={message}
                required
            />
            <input
                className="button hover"
                type="submit"
                value="envoyer"

            />
            <div className="form-message"></div>
        </form>
    )
}

export default ContactForm;
