import React, {useEffect} from 'react';
import { LinePieces } from 'three';

import { projects } from "../../data/projects-data";
import Project from "./Project";

const Projects = () => {

    const { project1, project2, project3} = projects;

    useEffect(()=>{
        // Get all images and texts, get the `canvas` element, and save slider length
        let sliderCanvas = document.querySelector('.pieces-slider__canvas');
        let imagesEl = [].slice.call(document.querySelectorAll('.pieces-slider__image'));
        let textEl = [].slice.call(document.querySelectorAll('.pieces-slider__text'));
        let slidesLength = imagesEl.length;

        // Define indexes related variables, as we will use indexes to reference items
        let currentIndex = 0, currentImageIndex, currentTextIndex, currentNumberIndex;
        let textIndexes = [];
        let numberIndexes = [];

        // Update current indexes for image, text and number
        function updateIndexes(){
            currentImageIndex = currentIndex * 3;
            currentTextIndex = currentImageIndex + 1;
            currentNumberIndex = currentImageIndex + 2;
        }
        updateIndexes();

        // Options for images
        let imageOption = {
            angle : 45, // rotate item pieces 45deg
            exraSpacing: { extraX: 100, extraY: 200}, // this extra spacing is needed to cover all the item, because angle != 0
            //pieceWidth: function(){ return Pieces.random(50, 200); }, // every piece will have a random width between 50px and 200px
            //ty: function(){ return Pieces.random( -400, 400 ); } // every piece will be translated in the Y axis a random distance between -400px and 400px
        }
    },[])




    return (
        <main className="projects-container">
            <div className="pieces-slider">
                <Project project={project1}/> 
                <Project project={project2}/> 
                <Project project={project3}/>

                <canvas className="pieces-slider__canvas"></canvas>

                <button className="pieces-slider__button pieces-slider__button--prev">prev</button>
                <button className="pieces-slider__button pieces-slider__button--next">next</button>
            </div>          
        </main>
    )
}

export default Projects;
