import React, {useEffect} from 'react';

import { projects } from "../../data/projects-data";
import Project from "./Project";

const Projects = () => {

    const { project1, project2, project3} = projects;

    useEffect(()=>{
        // START
    let sliderCanvas = document.querySelector('.pieces-slider__canvas');
    let imagesEl = [].slice.call(document.querySelectorAll('.pieces-slider__image'));
    let textEl = [].slice.call(document.querySelectorAll('.pieces-slider__text'));
    let slidesLength = imagesEl.length;
    })


    console.log(sliderCanvas, imagesEl, textEl, slidesLength)

    return (
        <main className="projects-container">
            <div className="pieces-slider">
                <Project project={project1}/> 
                <Project project={project2}/> 
                <Project project={project3}/>

                <canvas className="pieces-slider__canvas"></canvas>

                <button className="pieces-slider__button pieces-slider__button--prev">prev</button>
                <button className="pieces-slider__button pieces-slider__button--next">next</button>
            </div>          
        </main>
    )
}

export default Projects;
