import { render } from '@testing-library/react';
import React, { Component } from 'react'
import * as THREE from "three";
import { CSS3DObject, CSS3DRenderer } from 'three-css3drenderer';
import {OrbitControls} from "three/examples/jsm/controls/OrbitControls";
import { table } from "../data/skillsThree";


class TryThree extends Component {


    componentDidMount(){

        // THREE PARAMETRES
        let scene, camera, renderer, controls;
        const objects = [];
        const targets = {
            grid: [],
            helix: [],
            table:[],
            sphere:[]
        };

        const init = ()=>{
            const felidView = 40;
            const width = window.innerWidth/2;
            const height = window.innerHeight/2;
            const aspect = width / height;
            const nearPlane = 0.1;
            const farPlane = 1000;
            const WebGloutput = document.getElementById('container');

            scene = new THREE.Scene();
            camera = new THREE.PerspectiveCamera( felidView, aspect, nearPlane, farPlane);
            camera.position.z = 300;

            renderer = new CSS3DRenderer();
            renderer.setSize( width, height);
            renderer.domElement.style.position = 'absolute';
            WebGloutput.appendChild( renderer.domElement);
        }

        //ELEMENTS
        let i = 0;

        for(; i<table.length; i +=5 ) {
            const element = document.createElement('div');
            element.className = 'element';
            element.style.backgroundColor = `rgba( 0, 127, 127, ${ Math.random() * 0.5 + 0.25})`;

            const number = document.createElement('div');
            number.className = 'number';
            number.textContent = i / 5 + 1;
            element.appendChild(number);

            const symbol = document.createElement('div');
            symbol.className = 'symbol';
            symbol.textContent = table[ i ];
            element.appendChild(symbol);

            const detail = document.createElement('div');
            detail.className = 'detail';
            detail.innerHTML = `${ table[ i+1 ]} <br/> ${ table[ i+2 ]}}`;
            element.appendChild(detail);

            const object = new CSS3DObject( element );
            object.position.x = Math.random() * 400 - 200 ;
            object.position.y = Math.random() * 400 - 200 ;
            object.position.z = Math.random() * 400 - 200 ;

            console.log(scene)

            objects.push( object );
        };

    }


    render () {

        return (
            <div id="container">

                <div id="menu">
                    <button id="table">TABLE</button>
                    <button id="sphere">SPHERE</button>
                    <button id="sphere2">SPHERE2</button>
                    <button id="plane">PLANE</button>
                    <button id="helix">HELIX</button>
                    <button id="grid">GRID</button>
                </div>
            </div>
        )
    }
}

export default TryThree;
